import React, { Component } from 'react';
import {Link, Redirect} from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css'
import {test} from './store'
import {getProducts} from './store'
import {compose} from 'redux'
import {connect} from 'react-redux'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {withCookies} from 'react-cookie'
import Sidenavbar from "./sidenavbar"
import Inputdata from "./inputdata"
import queryString from 'query-string'
import './main.css'

class SearchApp extends Component {
  constructor(props) {
    const { cookies } = props;
    super(props); 
    this.state = { 
      products:[],
      search: '',
      username: " ",
      password: "",
      signupMessage: '', 
      isLoggedin: false,
      token: {},
      sizeparam: "",
     }
  }
  componentDidMount =()=>{

 //  this.props.submitsearcher(this.props.inputval)
  const parsedQuery = queryString.parse(this.props.location.search)
  console.log(parsedQuery)
   if(this.props.match.params.category === "fan" ){
    this.setState({sizeparam : "inches"})
   }
   else if(this.props.match.params.category === "refrigerator" || this.props.match.params.category === "microwave" || this.props.match.params.category === "blender" ){
    this.setState({sizeparam : "Litres"})
   }
   else if(this.props.match.params.category === "washing machine" || this.props.match.params.category === "microwave" ){
    this.setState({sizeparam : "Litres"})
   }
  }
  handleChange=(e)=>{
    this.setState({search:e.target.value})
  }
  render() {
    return ( 
          <div className="container">
            <Inputdata></Inputdata>
        <div className='row' >
        <div className="d-none d-lg-block col-lg-3 stick" style={{backgroundColor:"rgba(242, 242, 242,0.4)"}}>
          <Sidenavbar category={this.props.match.params.category}/>
        </div>
          <div className='col-12 col-lg-9'>
    <p>{this.props.searcher}</p>
          <div className='row' > 
        {this.props.searchedproducts.map((product) =>          
           <div className='col-6 col-md-4 col-lg-3'   key={product.productId} > 
                <center>
           <img className="mainImg img-responsive" src={require (`./images/${product.mainimg}`)} style={{maxWidth:"100%"}} ></img>
           </center>
        <small style={{float:"left"}}>{product.brand} </small><br/>
           <small style={{height:"40px"}}>
            <div  className="details">
    <Link to ={`/product/${product.productId}`} style={{color:'black'}}>
       {product.details.length > 50 ? product.details.slice(0,50)+ "..." : product.details +"-"+ product.model +"-"+ product.color}
       </Link>
        </div>
        <b>{product.mainprice}</b><br/>
        <div className="outer">
          <div className="inner" style={{width:`${product.percentrating || 0}%`}}>

          </div>
        </div> <small style={{fontSize:"12px"}}>({product.numOfRating || 0})</small>
         </small>
        <br/><br/>
        
           </div> 
           
         )}
         
             </div>
         </div>
        </div>
      </div>
     );
  }
}
 const mapStateToProps =(store)=>{
    return{           
       searchedproducts: store.searchedproducts,
       test:store.test,
       status:store.status,
       searcher: store.searcher,
       inputval: store.inputval,


     }
 }
 const mapDispatchToProps =(dispatch)=>{
   return{
     test: ()=> dispatch(test()),
     submitsearcher: (data)=> dispatch(submitsearcher(data))
   }
 }
 export default compose(withCookies, connect(mapStateToProps, mapDispatchToProps))(SearchApp);
