import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import {getfilteredSuggestions} from "./store"
import axios from 'axios';
import {searcher, submitsearcher} from './store'
import {connect} from 'react-redux';
import {withRouter} from "react-router-dom"
import {compose} from "redux"
import queryString from "query-string"

class Navbar extends Component {
    constructor(props) {
        super(props);
        this.state = { 
          inputval:"",
          showSuggestion:false,
          filteredSuggestions:[],
          suggestions:[],
          searcheddata: []
         }
    }
    componentDidMount = () =>{
      this.setState({inputval:this.props.inputval})
    }
   
    change2 = (e)=>{
      this.setState({inputval:e.target.value})
      const parsedQuery = queryString.parse(this.props.location.search)
      this.props.searcher(e.target.value)
         
     this.props.getfilteredSuggestions(e.target.value)

     if(e.target.value.length === 0){
     
      const url = window.location.href;
      const uri = url.split("?")[0]
      window.location.assign(uri)
     }
    }
    render() { 
    
        return ( 
            <div>
                <nav className="navbar">
                <div className="container">
              <div className="row">
                  <div className="col-2">
                    <img src={require("./images/fruget.jpg")} style={{width:"100%",height:"100%"}} alt=""/>
                  </div>
                  <div>

                  </div>
              </div>
              </div>
              </nav>
            </div>
         );
    }
}
