import React, { Component } from 'react';
import {connect} from "react-redux"
import {compose} from "redux"
import "bootstrap/dist/css/bootstrap.min.css"
import queryString from "query-string"
import {withRouter} from "react-router-dom"
import {submitsearcher} from "./store"
class Inputdata extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            val :[]
         }
    }
    componentDidMount = () => {
        let parsedQuery = queryString.parse(this.props.location.search);
        delete parsedQuery["page"]
        delete parsedQuery["sort"]
        if(parsedQuery.brand !== undefined){
        if(parsedQuery.brand.split(",").length > 1){
         var parsedbrand = parsedQuery.brand;
         delete parsedQuery["brand"];
         parsedbrand = parsedbrand.split(",")

         var val=  Object.values(parsedQuery);
         val = [...val, ...parsedbrand]
         this.setState({val})
        }
    }
         
            
         
    }
    render() { 
        return ( 
            <div>
                <div style={{textTransform:"capitalize",padding:"10px"}}>
                  <small style={{color:"lightgrey"}}> RESULT <span className="fa fa-arrow-right"></span></small> 
                  <div className="row" >
                  {this.state.val.map(value => 
                        <div key={value} className="col-1">
                         <div className="alert alert-primary" style={{padding:"2px"}}>
                         <small> {value.length > 6 ? value.slice(0,5)+"..." : value}</small>
                       </div>
                        </div>
                    )}
                      </div>
                </div>
            </div>
         );
    }
}
const mapStateToProps =(store)=>{
    return{           
       products: store.products,
       searching:store.searching,
       status:store.status,
       filteredSuggestions: store.filteredSuggestions,
       suggestions: store.suggestions,
       showSuggestions: store.showSuggestions,
       inputval: store.inputval
     }
  } 
 const mapDispatchToProps = (dispatch) =>{
     return{
         submitsearcher: (data) => dispatch(submitsearcher(data))
     }
 }
export default compose(withRouter, connect(mapStateToProps, mapDispatchToProps))(Inputdata);
