import React, { Component } from 'react';
import {connect} from "react-redux"
import 'bootstrap/dist/css/bootstrap.min.css'
class Suggestions extends Component {
    constructor(props) {
        super(props);
        this.state = {  }
    }
    render() { 
        return ( 
            <div className="container">
                <div className="row">
                    <div className="col-3"></div>
                    <center>
<div style={{display:`${this.props.inputval.length > 3 ? "block" : "none"}`,borderRadius:"5px",position:"absolute",backgroundColor:"white", top:"7%",padding:"15px",border:"1px solid grey", zIndex:"2"}}>
                <ul style={{listStyleType:"none", textAlign:"left"}}>
{ this.props.showSuggestions === true && this.props.inputval.length > 3 && this.props.filteredSuggestions.length > 0 ?
 this.props.filteredSuggestions.map(suggests =>
                <div key={suggests.maindetails}>
                   <a href="" style={{color:"black"}}><li>{suggests.maindetails}</li></a>  
                </div>
                ) : this.props.showSuggestions === false || this.props.inputval.length < 3 ? null :
                 <h3>No Suggestions! <small>Check your spellings and kindly search again </small></h3>}
                 </ul>
                    </div>
                    </center>
                </div>      
            </div>
            
         );
    }
}
 const mapStateToProps = (store) =>{
     return {
        products: store.products,
        searching:store.searching,
        status:store.status,
        filteredSuggestions: store.filteredSuggestions,
        suggestions: store.suggestions,
        showSuggestions: store.showSuggestions,
        inputval: store.inputval
     }
 }
export default connect(mapStateToProps)(Suggestions);
