import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import {getfilteredSuggestions} from "./store"
import axios from 'axios';
import {searcher} from './store'
import {connect} from 'react-redux';

class Navbar extends Component {
    constructor(props) {
        super(props);
        this.state = { 
          search: '',
          inputval:"",
          showSuggestion:false,
          filteredSuggestions:[],
          suggestions:[]
         }
    }
    change = (e)=>{
      this.setState({[e.target.name] : e.target.value},()=>{
         this.props.searcher(this.state.search)
      })   
    }
    componentDidMount =()=>{
      axios.get('http://localhost:5000/suggestions/suggestion')
      .then(res => this.setState({suggestions: res.data}) )
      .catch(err => console.warn(err))
    }
    change2 = (e)=>{
      this.setState({inputval:e.target.value})
      this.props.searcher(e.target.value)
     this.props.getfilteredSuggestions(e.target.value)
    }
    render() { 
     console.log("navbar props", this.props)
        return ( 
            <div>
              <nav className="navbar navbar-expand-md bg-dark navbar-dark">
  <a className="navbar-brand" >Navbar</a>
  <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#collapsibleNavbar">
    <span className="navbar-toggler-icon"></span>
  </button>
  <div className="collapse navbar-collapse" id="collapsibleNavbar">
    <ul className="navbar-nav">
      <li className="nav-item">
        <a className="nav-link" href="#">Link</a>
      </li>
      <li className="nav-item">
        <a className="nav-link" href="#">Link</a>
      </li>
      <li className="nav-item">
        <a className="nav-link" href="#">Link</a>
      </li>    
    </ul>
    <div className="col-4">
      <input type="text" className="form-control" value={this.state.search} name="search" onChange={this.change} placeholder="search goods and brands here..."/>
    </div>
    <div className="col-4">
      <input type="text" className="form-control" value={this.state.inputval} name="search" onChange={this.change2} placeholder="check suggestion here..."/>
    </div>
  </div>  
</nav>
            </div>
         );
    }
}
const mapStateToProps =(store)=>{
  return{           
     products: store.products,
     searching:store.searching,
     status:store.status,
     filteredSuggestions: store.filteredSuggestions,
     suggestions: store.suggestions,
     showSuggestions: store.showSuggestions,
     inputval: store.inputval
   }
}
const mapDispatchToProps =(dispatch)=>{
 return{
   searcher: (data)=> dispatch(searcher(data)),
   getfilteredSuggestions: (data) => dispatch(getfilteredSuggestions(data))
 }
}
export default connect(mapStateToProps,mapDispatchToProps)(Navbar);
